import React, { useState, useEffect } from "react";
import BodyHeader from "./bodyHeader";
import { DataGrid } from "@mui/x-data-grid";
import axios from "axios";



const columns = [
  { field: "sl_no", headerName: "Sl No", width: 85 },
  { field: "business_code", headerName: "Business Code" },
  { field: "cust_number", headerName: "Customer Number" },
  { field: "clear_date", headerName: "Clear Date", width: 120 },
  { field: "buisness_year", headerName: "Business Year" },
  { field: "doc_id", headerName: "Document ID", width: 120 },
  { field: "posting_date", headerName: "Posting Date", width: 120 },
  {
    field: "document_create_date",
    headerName: "Document Create Date",
    width: 110,
  },
  { field: "due_in_date", headerName: "Due in Date", width: 120 },
  { field: "invoice_currency", headerName: "Invoice Currency" },
  { field: "document_type", headerName: "Document Type" },
  { field: "posting_id", headerName: "Posting ID", width: 75 },
  { field: "total_open_amount", headerName: "Total Open Amount" },
  {
    field: "baseline_create_date",
    headerName: "Baseline Create Date",
    width: 110,
  },
  {
    field: "cust_payment_terms",
    headerName: "Customer Payment Terms",
    width: 150,
  },
  { field: "invoice_id", headerName: "Invoice ID", width: 120 },
  { field: "predict", headerName: "Predict" }
];

export const Body = (props) => {

  const [checkboxSelection, setCheckboxSelection] = useState(true);
  const [currentRow, setCurrentRow] = useState(false);
  const [pageSize, setPageSize] = useState(5);
  const [tableData, setTableData] = useState([]);
  const [searched, setSearched] = useState("");
  const [filteredData, setFilteredData] = useState([]);
  const [select, setSelection] = useState([]);
  const [adv_arr, setAdv_arr] = useState([]);

  const adv_Search = (advSearch_arr) => {
    console.log(advSearch_arr);

    if (advSearch_arr.length !== 0) {
      const filteredRows = tableData.filter((row) => {
        return (
          row.buisness_year
            .toString().toLowerCase()
            .includes(advSearch_arr[3].toLowerCase()) &&
          row.cust_number
            .toString()
            .toLowerCase()
            .includes(advSearch_arr[2].toLowerCase()) &&
          row.invoice_id
            .toString()
            .toLowerCase()
            .includes(advSearch_arr[1].toLowerCase()) &&
          row.doc_id.toLowerCase().includes(advSearch_arr[0].toLowerCase())
        );
      });
      setFilteredData(filteredRows);
      setAdv_arr(advSearch_arr);
    } else {
      setFilteredData(tableData);
    }
  };

  const searchItems = (searchVal) => {
    setSearched(searchVal);
    if (searched !== "") {
      const filteredRows = tableData.filter((row) => {
        return (
          row.cust_number
            .toString()
            .toLowerCase()
            .includes(searched.toLowerCase())
        );
      });
      setFilteredData(filteredRows);
    } else {
      setFilteredData(tableData);
    }
  };

  const handleRowSelection = (e) => {
    setSelection(e);
    if (e.length > 0) setCurrentRow(true);
    else setCurrentRow(false);
    console.log(select);
  };

  useEffect(() => {
    axios
      .get("http://localhost:8081/HRC60327WK-back_end/Fetch")
      .then((res) => {
        setTableData(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <div style={{ backgroundColor: "#283d4a" }}>
      <BodyHeader
        currentRow={currentRow}
        searchItems={searchItems}
        select={select}
        adv_Search={adv_Search}
      />

      <div style={{ height: 400, width: "100%" }}>
        {searched.length > 1 || adv_arr.length > 1 ? (
          <DataGrid
            getRowId={(r) => r.sl_no}
            sx={{
              ".MuiDataGrid-columnSeparator": {
                display: "none",
              },
              ".MuiDataGrid-row:nth-child(even)": {
                backgroundColor: "#2d4250",
              },
              ".MuiTablePagination-root, .MuiSvgIcon-root": {
                color: "#FFFFFF",
              },
              "& .MuiDataGrid-columnHeaderTitle": {
                overflow: "visible",
                lineHeight: "1.5rem",
                whiteSpace: "normal",
              },
              border: "none",
              color: "#FFFFFF",
              boxShadow: 5,
            }}
            rows={filteredData}
            columns={columns}
            pageSize={pageSize}
            onPageSizeChange={(newPageSize) => setPageSize(newPageSize)}
            rowsPerPageOptions={[5, 10, 20]}
            checkboxSelection={checkboxSelection}
            onSelectionModelChange={handleRowSelection}
            selectionModel={select}
            disableSelectionOnClick={true}
          />
        ) : (
          <DataGrid
            getRowId={(r) => r.sl_no}
            sx={{
              ".MuiDataGrid-columnSeparator": {
                display: "none",
              },
              ".MuiDataGrid-row:nth-child(even)": {
                backgroundColor: "#2d4250",
              },
              ".MuiTablePagination-root, .MuiSvgIcon-root": {
                color: "#FFFFFF",
              },
              "& .MuiDataGrid-columnHeaderTitle": {
                overflow: "visible",
                lineHeight: "1.5rem",
                whiteSpace: "normal",
              },
              // ".MuiDataGrid-columnHeaders":{
              //   paddingBottom: "0.5rem",
              // },
              border: "none",
              color: "#FFFFFF",
              boxShadow: 5,
            }}
            rows={tableData}
            columns={columns}
            pageSize={pageSize}
            onPageSizeChange={(newPageSize) => setPageSize(newPageSize)}
            rowsPerPageOptions={[5, 10, 20]}
            checkboxSelection={checkboxSelection}
            onSelectionModelChange={handleRowSelection}
            selectionModel={select}
            disableColumnFilter={true}
          />
        )}
      </div>
    </div>
  );
};
